mapper.select:
SELECT admi_div_cd 
FROM TBADMI_FLG 
WHERE admi_cd = #{admiCd}


   SELECT COALESCE(SUM(TOTAL_CNT),0) AS TOTAL_CNT
     FROM TBADMI_VISIT_UPJONG_INFO_D A
       ,  TBREGION B
    WHERE 1=1     
      AND A.ADMI_CD = B.ID
      AND B.RGN_CLSS ='H4'
   	  AND B.ID = #{admiCd}
      AND A.STDR_DATE >= #{startDate}
      AND A.STDR_DATE <= #{endDate}
